import React, { useRef, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { BrowserMultiFormatReader } from '@zxing/browser';
// eslint-disable-next-line no-unused-vars
import { motion } from 'framer-motion';

const pageVariants = {
  initial: { opacity: 0, scale: 0.98 },
  animate: { opacity: 1, scale: 1, transition: { duration: 0.3 } },
  exit: { opacity: 0, scale: 1.02, transition: { duration: 0.2 } },
};

export default function BarcodeScanner() {
  const videoRef = useRef(null);
  const fileInputRef = useRef(null);
  const navigate = useNavigate();
  const [isScanning, setIsScanning] = useState(true);
  const [loadingCode, setLoadingCode] = useState(null);

  useEffect(() => {
    if (!isScanning) return;

    let codeReader = new BrowserMultiFormatReader();
    let controls = null;

    const startCamera = async () => {
      try {
        controls = await codeReader.decodeFromConstraints(
          { video: { facingMode: 'environment' } },
          videoRef.current,
          (result, err) => {
            if (result && isScanning) {
              const barcode = result.getText();
              handleBarcodeDetected(barcode, controls);
            }
          }
        );
      } catch (err) {
        console.error("Error accessing camera:", err);
      }
    };

    startCamera();

    return () => {
      if (controls) {
        controls.stop();
      }
    };
  }, [isScanning]);

  const handleBarcodeDetected = async (barcode, controls) => {
    setIsScanning(false);
    setLoadingCode(barcode);
    if (controls) controls.stop();

    try {
      const res = await fetch(`https://world.openfoodfacts.org/api/v2/product/${barcode}.json`);
      const data = await res.json();
      
      if (data.status === 1 && data.product) {
        const prod = data.product;
        const productData = {
          barcode: barcode,
          name: prod.product_name || "Unknown Product",
          description: prod.brands || "Unknown Brand",
          calories: prod.nutriments ? Math.round(prod.nutriments['energy-kcal_100g'] || 0) : 0,
          protein: prod.nutriments ? Math.round(prod.nutriments.proteins_100g || 0) : 0,
          carbs: prod.nutriments ? Math.round(prod.nutriments.carbohydrates_100g || 0) : 0,
          fats: prod.nutriments ? Math.round(prod.nutriments.fat_100g || 0) : 0,
          image_url: prod.image_front_url || "https://lh3.googleusercontent.com/aida-public/AB6AXuAJ6ede5sWH1XnOfME5SWaYPeSIM6NrFPhjyHvtBVb2oEF8ds2BGkYxQkOibtgWNn5dQrlGRMGzrAXVFYSX-poPjjzLignXpIdEnqGLjQb28Imx1hSFd8vyPAIZroMcJ6oNqnmbU6xErqYhB4B_fHpcOfaFj09wIyiyPdES_NhCXWDVZ9k7n8ooxnO1OYBWFuogD03ZNJr0soYqV26GipaSCGF2TAa5Rxcxprk80mEBXjdnximGYGgNzH3tGtzWOLur97oBQN0ujgSW",
          raw_ingredients_text: prod.ingredients_text || ""
        };
        navigate('/barcode-result', { state: { productData } });
      } else {
        alert("Product not found in Open Food Facts database.");
        setIsScanning(true);
        setLoadingCode(null);
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching product data.");
      setIsScanning(true);
      setLoadingCode(null);
    }
  };

  const handleGalleryClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      // In a real app we'd decode the image file for barcodes here
      // For MVP, if they pick an image, we mock a response
      navigate('/barcode-result');
    }
  };

  return (
    <motion.div 
      className="bg-background-dark text-slate-100 font-display antialiased selection:bg-primary selection:text-surface-dark overflow-hidden h-screen w-full relative flex items-center justify-center"
      variants={pageVariants}
      initial="initial"
      animate="animate"
      exit="exit"
    >
      <div className="relative w-full max-w-[375px] h-[812px] max-h-screen bg-black sm:rounded-[40px] overflow-hidden shadow-2xl sm:border-[8px] border-zinc-800 flex flex-col justify-between">
        
        {/* Camera Feed Background Simulation */}
        <video ref={videoRef} className="absolute inset-0 w-full h-full object-cover z-0" />
        <input type="file" accept="image/*" className="hidden" ref={fileInputRef} onChange={handleFileChange} />
        <div className="absolute inset-0 z-0 pointer-events-none">
          <div className="absolute inset-0 bg-background-dark/30"></div>
        </div>

        {/* Loading Overlay */}
        {loadingCode && (
          <div className="absolute inset-0 z-50 bg-black/80 flex flex-col items-center justify-center p-6 text-center backdrop-blur-sm">
            <div className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full animate-spin mb-4"></div>
            <p className="text-white font-bold text-lg mb-2">Analyzing Product...</p>
            <p className="text-primary font-medium">{loadingCode}</p>
          </div>
        )}

        {/* Top Bar */}
        <div className="relative z-10 flex flex-col w-full bg-gradient-to-b from-black/60 to-transparent pt-12 pb-6 px-4">
          <div className="flex items-center justify-between">
            <button 
              onClick={() => navigate(-1)}
              className="flex size-10 items-center justify-center rounded-full bg-black/40 text-white backdrop-blur-sm hover:bg-black/60 transition-colors"
            >
              <span className="material-symbols-rounded" style={{ fontSize: '24px' }}>arrow_back</span>
            </button>
            <h2 className="text-white text-lg font-bold tracking-tight">Scan Product</h2>
            <button className="flex size-10 items-center justify-center rounded-full bg-black/40 text-white backdrop-blur-sm hover:bg-black/60 transition-colors group">
              <span className="material-symbols-rounded group-hover:text-primary transition-colors" style={{ fontSize: '24px' }}>flash_on</span>
            </button>
          </div>
          {/* Auto-focus Indicator */}
          <div className="flex justify-center mt-4">
            <div className="bg-black/30 backdrop-blur-md px-3 py-1 rounded-full border border-white/10 flex items-center gap-1.5">
              <span className="material-symbols-rounded text-primary animate-pulse" style={{ fontSize: '14px' }}>center_focus_strong</span>
              <span className="text-xs font-medium text-white/90">Auto-focus active</span>
            </div>
          </div>
        </div>

        {/* Center Scanner Area */}
        <div className="relative z-10 flex-1 flex flex-col items-center justify-center p-8">
          <div className="relative w-72 h-72 border-2 border-primary/30 rounded-3xl shadow-glow overflow-hidden cursor-pointer" onClick={() => navigate('/barcode-result')}>
            {/* Corner Accents */}
            <div className="absolute top-0 left-0 w-8 h-8 border-t-4 border-l-4 border-primary rounded-tl-xl"></div>
            <div className="absolute top-0 right-0 w-8 h-8 border-t-4 border-r-4 border-primary rounded-tr-xl"></div>
            <div className="absolute bottom-0 left-0 w-8 h-8 border-b-4 border-l-4 border-primary rounded-bl-xl"></div>
            <div className="absolute bottom-0 right-0 w-8 h-8 border-b-4 border-r-4 border-primary rounded-br-xl"></div>
            {/* Scanning Laser Line */}
            <div className="absolute inset-x-0 h-1/2 bg-gradient-to-b from-transparent to-primary/20 scanner-line">
              <div className="absolute bottom-0 w-full h-[2px] bg-primary shadow-[0_0_10px_#58ee2b]"></div>
            </div>
            {/* Helper Text inside frame */}
            <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
              <span className="text-white/50 text-sm font-medium text-center px-4">Align barcode here<br/><span className="text-xs text-primary mt-2 block">(Tap to mock scan)</span></span>
            </div>
          </div>
          <button className="mt-8 text-white/80 text-sm font-medium hover:text-primary transition-colors flex items-center gap-2 bg-black/20 px-4 py-2 rounded-full backdrop-blur-sm border border-white/5">
            <span className="material-symbols-rounded" style={{ fontSize: '18px' }}>keyboard</span>
            Enter barcode manually
          </button>
        </div>

        {/* Bottom Area */}
        <div className="relative z-10 w-full px-6 pb-12 pt-4 flex flex-col gap-6 bg-gradient-to-t from-black via-black/80 to-transparent">
          
          {/* Action Buttons */}
          <div className="flex items-center justify-center gap-8 px-4 z-50 relative">
            <button onClick={handleGalleryClick} className="flex flex-col items-center gap-1 group cursor-pointer z-50">
              <div className="w-12 h-12 rounded-full bg-black/40 backdrop-blur-md flex items-center justify-center border border-white/10 group-hover:border-primary/50 transition-colors">
                <span className="material-symbols-rounded text-white/80 group-hover:text-primary" style={{ fontSize: '24px' }}>photo_library</span>
              </div>
              <span className="text-xs font-medium text-white/60">Gallery</span>
            </button>
            <button className="flex flex-col items-center gap-1 group">
              <div className="w-16 h-16 rounded-full bg-primary/20 backdrop-blur-md flex items-center justify-center border-2 border-primary shadow-[0_0_20px_rgba(88,238,43,0.3)] transform group-active:scale-95 transition-all">
                <div className="w-12 h-12 rounded-full bg-primary flex items-center justify-center">
                  <span className="material-symbols-rounded text-surface-dark" style={{ fontSize: '28px' }}>barcode_scanner</span>
                </div>
              </div>
            </button>
            <button className="flex flex-col items-center gap-1 group">
              <div className="w-12 h-12 rounded-full bg-black/40 backdrop-blur-md flex items-center justify-center border border-white/10 group-hover:border-primary/50 transition-colors">
                <span className="material-symbols-rounded text-white/80 group-hover:text-primary" style={{ fontSize: '24px' }}>history</span>
              </div>
              <span className="text-xs font-medium text-white/60">History</span>
            </button>
          </div>

          {/* Mode Selector */}
          <div className="flex justify-center gap-8 text-white/50 text-xs font-bold uppercase tracking-widest pb-4">
            <span className="text-white relative">
              Barcode
              <span className="absolute -bottom-2 left-1/2 -translate-x-1/2 w-1 h-1 bg-white rounded-full"></span>
            </span>
            <Link to="/scanner" className="hover:text-white transition-colors">Meal</Link>
            <span className="hover:text-white transition-colors cursor-pointer">Recipe</span>
          </div>
        </div>

      </div>
    </motion.div>
  );
}
